let startTime,animationTime=15,percentContainer=document.querySelector(".percent span"),loadingLine=document.querySelector(".loading-line .loading-line__done");const animation=()=>{startTime=startTime||new Date;let e=new Date;e-startTime<1e3*animationTime&&(percentContainer.innerHTML=Math.round((e-startTime)/(1e3*animationTime)*100),loadingLine.style.width=(e-startTime)/(1e3*animationTime)*100+"%",requestAnimationFrame(animation))};window.addEventListener("load",()=>{requestAnimationFrame(animation)});